  /* @jsx mdx */

export const rightToc = [];
const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {
  rightToc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <p>{`Adding my own classes to ml5 mobile net`}</p>
    {
      /*truncate*/
    }
    <p>{`Todays video I followed `}<a parentName="p" {...{
        "href": "https://www.youtube.com/watch?v=eeO-rWYFuG0&list=PLRqwX-V7Uu6YPSwT06y_AEYTqIwbeam3y&index=6"
      }}>{`here`}</a></p>
    <p>{`So the first thing that has to be done is make the image classifier into a
feature extractor, which is as simple as doing`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-js"
      }}>{`classifier = mobilenet.classification(videoToPredict,videoReady)
`}</code></pre>
    <p>{`Where the first parameter `}<inlineCode parentName="p">{`videoToPredict`}</inlineCode>{` is the video and `}<inlineCode parentName="p">{`videoReady`}</inlineCode>{` is just
a callback function.`}</p>
    <p>{`Now I have to add buttons to tell the classifier what class I am showing on the
video and a train button to tell it to train on the images captured`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-js"
      }}>{`schoolDuckButton = createButton('School Duck')
schoolDuckButton.mousePressed(function () {
    classifier.addImage('School Duck')
})
policeDuckButton = createButton('Police Duck')
policeDuckButton.mousePressed(function () {
    classifier.addImage('Police Duck')
})
trainButton = createButton('Train')
trainButton.mousePressed(function() {
    classifier.train(whileTraining)
})
`}</code></pre>
    <p>{`The `}<inlineCode parentName="p">{`whileTraining`}</inlineCode>{` is given the loss and as its training it gets lower and
lower. In this image it started at 1.37065 and went down to 0.0`}</p>
    <p><img parentName="p" {...{
        "src": "/img/tutorial/feature_extractor/loss1.PNG",
        "alt": "try1"
      }}></img></p>
    <p>{`So whenever the loss value gets to `}<inlineCode parentName="p">{`null`}</inlineCode>{` I run the classifer to show the
results and predict whatever is shown on the screen. Here are the results.`}</p>
    <p>{`The two classes
`}<img parentName="p" {...{
        "src": "/img/tutorial/feature_extractor/schoolDuck.PNG",
        "alt": "try1"
      }}></img>{`
`}<img parentName="p" {...{
        "src": "/img/tutorial/feature_extractor/policeDuck.PNG",
        "alt": "try1"
      }}></img></p>
    <p>{`A cat
`}<img parentName="p" {...{
        "src": "/img/tutorial/feature_extractor/cat.PNG",
        "alt": "try1"
      }}></img></p>
    <p>{`A brown duck? A monkey duck?
`}<img parentName="p" {...{
        "src": "/img/tutorial/feature_extractor/brownDuck.PNG",
        "alt": "try1"
      }}></img></p>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
